(860, 'Giáo trình đồ họa máy tính', '    \nGiáo trình đồ họa máy tính gồm 5 chương, 3 nội dung chính là trình bày các thuật toán vẽ và tô màu các đường thẳng cơ bản; các phép biến...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778528-giao-trinh-do-hoa-may-tinh', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_27.jpg', 0, 0, 0, 0, 0),
(861, 'Sổ tay Revit 2013', '    \nCuốn sổ tay ngắn gọn, trình bày đơn giản, dễ hiểu, trực tiếp vào những điều cụ thể ứng dụng vào thực tế làm việc, thích hợp với những ai đang tìm hiểu và muốn biết một cách tổng...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778434-so-tay-revit-2013', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/so-tay-revit-2013-778434.png', 0, 0, 0, 1, 0),
(862, 'Bài giảng Mastercam X', '    \n1 Cài đặt Mastercam và các modul ứng dụng 2 Khởi chạy Mastercam 3 Tìm hiểu menu màn hình và các chuyển đổi giữa chúng 4 Tìm hiểu các lệnh save , file , exit và 1...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778362-bai-giang-mastercam-x', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_17.jpg', 0, 0, 0, 0, 0),
(863, 'Hướng dẫn giải bài tập sức bền vật liệu sử dụng MDSolids', '    \n1 Giúp sinh viên giải được tất cả các dạng bài tập cơ bản trong sức bền vật liệu 2 Giúp kiểm tra lại các kết quả đã tính bằng tay 3 Cung cấp hình ảnh minh học nội lực ứng suất.. của...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778360-huong-dan-giai-bai-tap-suc-ben-vat-lieu-su-dung-mdsolids', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_12.jpg', 0, 0, 0, 0, 2),
(864, 'Bài giảng Autocad 2D và 3D', '    \nAutocad được là 1 phần mềm thiết kế không thể thiếu cho sinh viên các khối ngành kĩ thuật ,đặc biệt là những ngành liên quan đến xây dựng , kiến trúc...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778269-bai-giang-autocad-2d-va-3d', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/bai-giang-autocad-2d-va-3d-778269.jpg', 0, 0, 0, 0, 0),
(865, 'Giáo Trình Kĩ Thuật Đồ Họa', '    \nCác kiến thức cơ bản cần thiết để học chương này bao gồm :\n- Các khái niệm toán học về đường thẳng như : đường thẳng là gì : dạng tổng quát phương trình đường thẳng, hệ số  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/778103-giao-trinh-ki-thuat-do-hoa', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_21.jpg', 0, 0, 0, 0, 0),
