(868, 'Tự học llustrator CS6', '    \nTự học llustrator CS6 (Sưu tầm và biên soạn) Khi lần đầu khởi động Illustrator, bạn sẽ thấy một không gian làm việc (Wokrspace) bao gồm một thanh ứng dụng ở trên cùng với khung ứng dụng bên dưới...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/776055-tu-hoc-llustrator-cs6', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-6.jpg', 0, 0, 0, 0, 0),
(869, 'Tạo ảnh họa tiết theo phong cách Grungy với Photoshop và illustrator', '    \nTạo ảnh họa tiết theo phong cách Grungy với Photoshop và illustrator Tác giả Teddy Soegiarto quan niệm rằng thiết kế luôn bắt nguồn từ cảm hứng , và tác giả nói rằng “Các cảm hứng thiết kế của...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/776054-tao-anh-hoa-tiet-theo-phong-cach-grungy-voi-photoshop-va-illustrator', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(870, 'Lý Thuyết Màu Sắc Trong Thiết Kế Web', '    \nKhi thiết kế web mọi người thường quan tâm đến thiết kế đồ họa ( LOGO) và nội dung web. Nhưng màu sắc cũng là phần quan trọng không kém khi thiết kế website<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/775738-ly-thuyet-mau-sac-trong-thiet-ke-web', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(871, 'Actions trong Photoshop', '    \nĐộng lực khiến mình bỏ công nghiên cứu và soạn thảo đề tài nầy vì thấy nó quá hay . Mình được cái may mắn là thừa hường một gia tài đồ sộ của các bậc vĩ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/775696-actions-trong-photoshop', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-16.jpg', 0, 0, 0, 0, 0),
(872, 'giáo trình thư viện đồ họa Glut trong C++', '    \nGiáo trình về thư viện đồ họa Glut trong lập trình C++<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/775515-giao-trinh-thu-vien-do-hoa-glut-trong-c', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_8.jpg', 0, 0, 0, 0, 0),
(873, 'Macromedia Flash MX', '    \nFlash là ứng dụng diễn hoạt dành cho web hiện đang sử dụng phổ biến trên nhiều website. Diễn hoạt với Flash không hề khó, chỉ cần bạn có ý tưởng. Tài liệu gồm 8 bài học này sẽ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/773887-macromedia-flash-mx', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_27.jpg', 0, 0, 0, 0, 0),
(874, 'Giáo trình Autocad 3D', '    \nGiáo trình cơ bản về Autocad 3D của trung tâm đào tạo và phát triển phần mềm tin học tri thức. Gồm 9 bài với nội dung ngắn gọn, có minh họa rõ ràng<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772777-giao-trinh-autocad-3d', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_6.jpg', 0, 0, 0, 0, 0),
(875, 'Tổng quan cơ sở đồ họa máy tính', '    \nGiáo trình có các nội dung như sau: Tổng quan về độ họa máy tính. Các kỹ thuật vẽ trên màn hình đồ họa như: vẽ đường thẳng, đường tròn, elip, vẽ đồ thị hàm số....<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772680-tong-quan-co-so-do-hoa-may-tinh', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(876, 'Giáo trình xử lý ảnh trong photoshop khi sử dụng công cụ và chế độ quick mash', '    \nCung cấp các bài tập thực hành giúp làm quen Photoshop CS và Image Ready từ căn bản đến nâng cao, với hình ảnh giúp bạn dễ hiểu và ghi nhớ nhanh hơn...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772721-giao-trinh-xu-ly-anh-trong-photoshop-khi-su-dung-cong-cu-va-che-do-quick-mash', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(877, 'Giáo trình Autocad 2004', '    \nGiáo trình hướng dẫn sử dụng autocad 2004, gồm các cách thao tác cơ bản trên thanh công cụ cũng như các lệnh và phím tắt mà người làm việc với autocad thường...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772631-giao-trinh-autocad-2004', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_14.jpg', 0, 0, 0, 0, 0),
(878, 'Các thuật toán giấu thông tin trong file Ảnh _ĐHQG_KHOA CÔNG NGHỆ', '    \nGiấu thông tin là nghệ thuật nhúng mẩu tin mật vào một vật mang tin khác. Giấu tin trong ảnh số là giấu các mẩu tin cũng là dạng số trong máy tính vào các ảnh nhị...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772108-cac-thuat-toan-giau-thong-tin-trong-file-anh-_dhqg_khoa-cong-nghe', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/biasach_772108.jpg', 0, 0, 0, 0, 0);
