(879, 'Tiểu luận về các phương pháp xác định biên ảnh_ĐHQG_KHOA CÔNG NGHỆ', '    \nTiểu luận trình bày các phương pháp phát hiện biên ảnh trong kỹ thuật xử lý ảnh như: phương pháp Gradient, Laplace, gián tiếp, trực tiếp,..<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/772101-tieu-luan-ve-cac-phuong-phap-xac-dinh-bien-anh_dhqg_khoa-cong-nghe', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/biasach_772101.jpg', 0, 0, 0, 0, 0),
(880, 'Giáo Trình Xử Lý Ảnh_GS Nguyễn Văn Ngọ_ĐHQG_KHOA CÔNG NGHỆ', '    \nGiáo Trình Xử Lý Ảnh của Giáo Sư Nguyễn Văn Ngọ ĐHQG Khoa Công Nghệ bao gồm 4 chương về đồ họa ứng dụng: Cở sở Xử Lý Ảnh, Cải thiện ảnh,Phục hồi ảnh,Mã hóa...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/771785-giao-trinh-xu-ly-anh_gs-nguyen-van-ngo_dhqg_khoa-cong-nghe', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/biasach_771785.jpg', 0, 0, 0, 0, 1),
