(885, 'Tài liệu đồ họa máy tính - CÁC PHÉP BIẾN ĐỔI TRONG ĐỒ HỌA BA CHIỀU', '    \nCác phép biến đổi trong đồ họa ba chiều là sự mở rộng của các phép biến đổi trong đồ họa hai chiều bằng cách thêm vào việc xem xét tọa độ thứ ba, tọa độ z. Bây giờ, chúng  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/771070-tai-lieu-do-hoa-may-tinh-cac-phep-bien-doi-trong-do-hoa-ba-chieu', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-6.jpg', 0, 0, 0, 0, 0),
(886, 'Tài liệu đồ họa máy tính - Các đối tượng đồ họa cơ sở', '    \nVới các ảnh đồ họa phát sinh bằng máy tính, hình dạng và màu sắc của mỗi đối tượng có thể được mô tả riêng biệt bằng hai cách : hoặc là bằng dãy các...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/771069-tai-lieu-do-hoa-may-tinh-cac-doi-tuong-do-hoa-co-so', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-16.jpg', 0, 0, 0, 0, 0),
(887, 'Tài liệu đồ họa máy tính', '    \nMột số thuật toán được gọi chính xác hơn là các thuật toán mặt hiện ( visible surface algorithms), nhưng tuy nhiên cả hai tên đều được dùng thay nhau.\n<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/771067-tai-lieu-do-hoa-may-tinh', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-16.jpg', 0, 0, 0, 0, 0),
(888, 'Full Bài Giảng Đồ Họa Máy Tính', '    \nBài giảng môn ĐỒ HỌA MÁY TÍNH\n<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/771038-full-bai-giang-do-hoa-may-tinh', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-6.jpg', 0, 0, 0, 0, 0),
(889, 'Lập trình đồ họa với AWT (Tiếng Việt)', '    \nAWT là viết tắt của Abstract Windowing Toolkit. AWT là một bộ các lớp trong Java cho phép chúng ta tạo GUI và chấp nhận các nhập liệu của người dùng thông qua bàn phím...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/770897-lap-trinh-do-hoa-voi-awt-tieng-viet', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(890, 'Giáo trình Kỹ thuật Đồ họa', '    \nTrình bày các thuật toán vẽ và tô các đường cơ bản như đường thẳng, đa giác, đường tròn, ellipse và các đường conic. Các thuật toán này giúp cho...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/770563-giao-trinh-ky-thuat-do-hoa', '', 'http://tailieuhoctap.vn/images/bia_sach/cong-nghe-thong-tin/cong-nghe-thong-tin-2.jpg', 0, 0, 0, 0, 0),
(891, 'Ánh sáng và màu sắc', '    \nChúng ta đang sống trong một thế giới đầy màu sắc. Với sự trợ giúp của màu sắc chúng ta có thể nhìn nhận rõ ràng mọi vật xung quanh để làm cho cảm giác của chúng ta...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/770491-anh-sang-va-mau-sac', '', 'http://tailieuhoctap.vn/images/books/cong-nghe-thong-tin/do-hoa-ung-dung/biasach_770491.jpg', 0, 0, 0, 0, 0),
