(1100, '10 nguyên tắc thiết kế tốt của Dieter Rams', '    \n Dựa trên kinh nghiệm của mình, nhà thiết kế nổi tiếng Dieater Rams đã chắt lọc những yếu tố cần thiết của triết lý thiết kế của ông vào mười nguyên tắc. <br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67615-10-nguyen-tac-thiet-ke-tot-cua-dieter-rams-phan-1-', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_6.jpg', 0, 0, 0, 0, 0),
(1101, 'Hiệu Ứng Sóng Gợn Lăn Tăn', '    \n  Hiệu ứng nước gợn sóng lăn tăn có thể dễ dàng tạo ra với một Radial Grdient và tạo các giọt nước rơi với Gradient Mesh Tool. <br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67607-hieu-ung-song-gon-lan-tan', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_24.jpg', 0, 0, 0, 0, 0),
(1102, 'Icon Logo Giọt Nước trên Chiếc Lá Phong', '    \nIcon Logo Giọt Nước trên Chiếc Lá Phong Hôm nay tôi sẽ trình bày cách tạo một icon logo hình giọt nước trên chiếc lá phong trong Photoshop. [1] Chọn một file mới có kích thước 450 pixels, 500 pixels, độ phân giải 72 dpi trong RGB mode....<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67608-icon-logo-giot-nuoc-tren-chiec-la-phong', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_2.jpg', 0, 0, 0, 0, 4),
(1103, 'Hướng Dẫn Photoshop: Lâu Đài Trên Mây ', '    \n Trong hướng dẫn này, chúng ta sẽ học cách tạo một lầu đài kỳ lạ trên bầu trời. Với cảnh tượng hùng vĩ, lạ mắt và độc được tạo ra, hi vọng sẽ làm bạn thích thú. <br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67606-huong-dan-photoshop-lau-dai-tren-may-phan-1-', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_7.jpg', 0, 0, 0, 0, 0),
(1104, 'Hướng dẫn tạo chữ nhòe mực bằng Photoshop', '    \nHướng dẫn tạo chữ nhòe mực  Trong bài hướng dẫn này bạn dễ dàng có thể tạo một hiệu ứng chữ nhòe mực nước rất đẹp.  Bắt đầu với Photoshop và tạo một file mới. Bạn hãy chọn kích thước landscape (nằm ngang). Sau đó chọn 1  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67602-huong-dan-tao-chu-nhoe-muc-bang-photoshop', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_12.jpg', 0, 0, 0, 0, 0),
(1105, 'Hướng dẫn vẽ biểu tượng sống động với Illustrator', '    \nHướng dẫn vẽ biểu tượng sống động với Illustrator Từng bước thực hiện để tạo ra một biểu tượng sống động, đầy màu sắc mang phong cách đồ họa bằng Illustrator. Mở Illustrator, tạo tài liệu mới. Vẽ các hình tròn như hình  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67603-huong-dan-ve-bieu-tuong-song-dong-voi-illustrator', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_25.jpg', 0, 0, 0, 0, 0),
