(1107, 'Giới thiệu Layers trong photoshop ', '    \nMột trong những thành phần chuẩn của Photoshop là Layers, nó chính là nền tảng để bạn có thể tạo nên những bức ảnh đẹp. Trong bài này, Quản Trị Mạng sẽ giới thiệu một vài thao tác sử dụng layer cơ bản để làm nổi bật một bức  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67592-gioi-thieu-layers-trong-photoshop', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_14.jpg', 0, 0, 0, 0, 0),
(1108, 'Hướng Dẫn Photoshop: Tạo Chữ 3D trong Photoshop', '    \nHướng Dẫn Photoshop: Tạo Chữ LOVE 3D trong Photoshop .Trong hướng dẫn này, tôi sẽ trình bày cách tạo ra hiệu ứng 3D text tuyệt đẹp bằng cách sử dụng các Texture khác nhau và sử dụng Xsara3D &amp; Photoshop. <br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67574-huong-dan-photoshop-tao-chu-love-3d-trong-photoshop-phan-1-', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_25.jpg', 0, 0, 0, 0, 0),
(1109, 'Hiệu Ứng Laser Tỏa Sáng trong Photoshop', '    \nTrong bài này, tôi sẽ chỉ bạn cách tạo ra một chùm tia laser dựa trên hiệu ứng tỏa sáng và cách đặt nó trên cơ thể con người. Chúng ta bắt đầu bằng cách tìm một bức ảnh phù hợp với tác phẩm....<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67551-hieu-ung-laser-toa-sang-trong-photoshop', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_12.jpg', 0, 0, 0, 0, 0),
(1110, 'Hiệu Ứng Chữ Phim “Clash of the Titans” trong Photoshop ', '    \nHiệu Ứng Chữ Phim “Clash of the Titans” trong Photoshop (phần 1)  Một vài tuần trước, tôi đã xem bộ phim Clash of the Titans. Bộ phim này cũng được, tôi phải thừa nhận vậy, tôi đã mong đợi nhiều hơn nữa khi thấy những con quái vật khổng  ...<br>     ', NULL, '/chi-tiet-sach/186-nganh-cong-nghe-thong-tin/do-hoa-ung-dung/67558-hieu-ung-chu-phim-clash-of-the-titans-trong-photoshop', '', 'http://tailieuhoctap.vn/images/bia_sach/cover_2.jpg', 0, 0, 0, 0, 0),
